import React from 'react'
import { push } from 'connected-react-router'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import {
  editProductDetails
} from '../../modules/prodDetails'
import '../home/home.css';
const renderTableData=(data,changePage)=>{
  return data.map((pFVal, index) => {
    console.log(pFVal);
     return (
        <tr key={index}>
           <td>{pFVal.name}</td>
           <td>{pFVal.weight}</td>
           <td>{pFVal.availability}</td>
           <td>{pFVal.isEditable?<button type="button" onClick={(e)=>{e.preventDefault();changePage(index)}}>Edit</button>:''}</td>
        </tr>
     )
  })
}
const enableSubmit=()=>
{
    let reqFi=['name','weight','produrl']

}

class EditProductForm extends React.Component
{
    constructor(props)
    {
        super(props);
        this.state = {priceTier:''}
        console.log(props);
    }
    handleCheckBox = (id)=>{
        document.getElementById("eyes").checked = false;
        document.getElementById("eno").checked = false;
        document.getElementById(id).checked = true;
    }
    handlePriceRange()
    {
        let res = [];
        if(this.state.priceTier=='budget')
        {
            this.props.pricingInfo.budget.map((val,index)=>
            {
                res.push(<option key={index} value={val}>val</option>
                )

            })
            return<select id="budgetOptions">{res}</select>
        }
       else if(this.state.priceTier=='premier')
        {
            this.props.pricingInfo.premier.map((val,index)=>
            {
                res.push(<option key={index} value={val}>val</option>
                )

            })
            return<select id="premierOptions">{res}</select>
        }
        else
        return
    }
    render()
    {
        return(
<div>
<table>
  <thead>
  <tr>
    <th>FieldName</th>
    <th>Type</th>
    <th>Comments</th>
  </tr>
  </thead>
  <tbody>
  <tr>
    <td>Name</td>
    <td><input id="name" type="text"></input></td>
    <td>Required</td>
  </tr>
  <tr>
    <td>Weight</td>
    <td><input id="weight" type="text"></input></td>
    <td>Required</td>
  </tr>
  <tr>
    <td>Availability</td>
    <td><input id="avail" type="number" min="0"></input></td>
    <td>Optional</td>
  </tr>
  <tr>
    <td>Product Url</td>
    <td><input id="produrl" type="url"></input></td>
    <td>Required</td>
  </tr>
  <tr>
    <td>Price Tier</td>
    <td><input type="radio" onClick={()=>{this.setState({priceTier:'budget'})}} name="Tier" id="budget" value="budget" /><span style={{marginLeft:'5px',marginRight:'5px'}}>budget</span>
  <input type="radio" onClick={()=>{this.setState({priceTier:'premier'})}} id="premier" name="Tier" value="premier" /><span style={{marginLeft:'5px',marginRight:'5px'}}>premier</span></td>
    <td>Required</td>
  </tr>
  <tr>
    <td>Price Range</td>
    <td>
    {
        this.handlePriceRange()
    }
</td>
    <td>Required</td>
  </tr>
  <tr>
    <td>Is Editable</td>
    <td>  <input style={{marginLeft:'5px',marginRight:'5px'}} type="checkbox" id="eyes" value="true" onClick={(e)=>{this.handleCheckBox(e.target.id)}} />True
  <input style={{marginLeft:'5px',marginRight:'5px'}} type="checkbox" id="eno" name="vehicle2" value="false" onClick={(e)=>{this.handleCheckBox(e.target.id)}} />False</td>
    <td>Required</td>
  </tr>
  </tbody>
</table>
<div style={{'margin':'10px','textAlign':'center'}}>
<button type="button" onClick={()=>{console.log('CLICKEDDD')}} disabled>Submit</button>
</div>
</div>)

    }
}

const mapStateToProps = ({ prodDetails }) => ({
products:prodDetails.products,
pricingInfo:prodDetails.pricingInfo
})

const mapDispatchToProps = dispatch =>
  bindActionCreators(
    {
      editProductDetails,
      changePage: (index) => push('/edit-product?index='+index)
    },
    dispatch
  )

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(EditProductForm)
